mapper.select:

     
     	select mem_nick, mem_content, NVL(mem_profile, 'resources/image/nothing.jpg') as mem_profile, mem_totalrc, mem_monthrc
     	from members
     	where mem_id = #{mem_id}

mapper.update:

     
     	update members
     	set mem_nick = #{mem_nick},
     		mem_profile = #{mem_profile},
     		mem_content = #{mem_content}
     	where mem_id = #{mem_id}
